contracts/BorrowProtocol.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

interface IYieldPool {
    function isTokenAllowed(address _address) external view returns (bool);
}

contract BorrowProtocol is ReentrancyGuard, Ownable {
    using SafeERC20 for IERC20;

    IYieldPool public yieldPool;

    struct Loan {
        uint256 loanId;
        uint256 collateralAmount;
        address collateralToken;
        address borrowToken;
        uint256 borrowAmount;
        uint256 duration;
        uint256 startTime;
        uint256 interestRate;
        address userAddress;
        bool active;
    }

    mapping(address => mapping(uint256 => Loan)) public userLoans;
    mapping(address => uint256) public liquidationThresholds;
    mapping(address => uint256[]) public userLoanIds;

    uint256 public minHealthFactor = 150;
    uint256 public minimumCollateralAmount = 1 ether;
    uint256 public minimumDuration = 1 days;
    uint256 public currentLoanId;

    event CollateralDeposited(
        address indexed user,
        address indexed token,
        uint256 amount
    );
    event LoanCreated(
        address indexed user,
        uint256 loanId,
        uint256 amount,
        address token,
        uint256 duration
    );
    event LoanRepaid(address indexed user, uint256 loanId, uint256 amount);
    event CollateralWithdrawn(
        address indexed user,
        uint256 loanId,
        uint256 amount
    );
    event LoanLiquidated(
        address indexed user,
        uint256 loanId,
        address liquidator
    );

    constructor(address _yieldPoolAddress, address _owner) Ownable(_owner) {
        yieldPool = IYieldPool(_yieldPoolAddress);
    }

    function setMinHealthFactor(uint256 _minHealthFactor) external onlyOwner {
        minHealthFactor = _minHealthFactor;
    }

    function setLiquidationThreshold(
        address token,
        uint256 threshold
    ) external onlyOwner {
        require(threshold <= 100, "Threshold must be <= 100%");
        liquidationThresholds[token] = threshold;
    }

    function setMinCollateralAmount(
        uint256 _minimumCollateralAmount
    ) external onlyOwner {
        minimumCollateralAmount = _minimumCollateralAmount;
    }

    function setMinimumDuration(uint256 _minimumDuration) external onlyOwner {
        minimumDuration = _minimumDuration;
    }

    function calculateHealthFactorSimulated(
        uint256 _collateralAmount,
        uint256 _borrowAmount
    ) public pure returns (uint256) {
        if (_borrowAmount == 0) return type(uint256).max;
        return (_collateralAmount * 100) / _borrowAmount;
    }

    function calculateTotalDue(
        address user,
        uint256 loanId
    ) public view returns (uint256) {
        Loan storage loan = userLoans[user][loanId];
        uint256 timeElapsed = block.timestamp - loan.startTime;
        // Calculate interest: principal * rate * time / (100% * 365 days)
        uint256 interest = (loan.borrowAmount *
            loan.interestRate *
            timeElapsed) / (10000 * 365 days);

        return loan.borrowAmount + interest;
    }

    function getMinHealthFactor() public view returns (uint256) {
        return minHealthFactor;
    }

    function Borrow(
        address _collateralToken,
        uint256 _collateralAmount,
        address _borrowToken,
        uint256 _borrowAmount,
        uint256 _duration,
        uint256 _interestRate
    ) external nonReentrant {
        require(
            yieldPool.isTokenAllowed(_collateralToken),
            "collateralToken not allowed"
        );
        require(
            yieldPool.isTokenAllowed(_borrowToken),
            "borrowToken not allowed"
        );
        require(_interestRate > 0, "invalid interest rate");
        require(
            _collateralAmount >= minimumCollateralAmount,
            "collateral too low"
        );
        require(_borrowAmount > 0, "borrow amount is 0");
        require(_duration >= minimumDuration, "duration too short");

        uint256 healthFactor = calculateHealthFactorSimulated(
            _collateralAmount,
            _borrowAmount
        );
        require(healthFactor >= minHealthFactor, "Health factor too low");

        IERC20(_collateralToken).safeTransferFrom(
            msg.sender,
            address(this),
            _collateralAmount
        );
        IERC20(_borrowToken).safeTransfer(msg.sender, _borrowAmount);

        uint256 loanId = currentLoanId++;

        Loan memory newLoan = Loan({
            loanId: loanId,
            collateralAmount: _collateralAmount,
            collateralToken: _collateralToken,
            borrowToken: _borrowToken,
            borrowAmount: _borrowAmount,
            duration: _duration,
            startTime: block.timestamp,
            interestRate: _interestRate,
            userAddress: msg.sender,
            active: true
        });
        userLoanIds[msg.sender].push(loanId);
        userLoans[msg.sender][loanId] = newLoan;

        emit LoanCreated(
            msg.sender,
            loanId,
            _borrowAmount,
            _borrowToken,
            _duration
        );
        emit CollateralDeposited(
            msg.sender,
            _collateralToken,
            _collateralAmount
        );
    }

    function getUserLoans(address user) public view returns (Loan[] memory) {
        uint256[] memory ids = userLoanIds[user];
        Loan[] memory result = new Loan[](ids.length);

        for (uint i = 0; i < ids.length; i++) {
            result[i] = userLoans[user][ids[i]];
        }

        return result;
    }

    function payLoan(uint _loanId, uint256 _amount) external nonReentrant {
        Loan storage loan = userLoans[msg.sender][_loanId];
        require(loan.loanId == _loanId, "Loan not found");
        require(loan.active, "Loan is not active");
        require(
            block.timestamp <= loan.startTime + loan.duration,
            "Loan expired"
        );

        uint256 totalDue = calculateTotalDue(msg.sender, _loanId);

        if (_amount >= totalDue) {
            // User is fully repaying the loan with interest
            IERC20(loan.borrowToken).safeTransferFrom(
                msg.sender,
                address(this),
                totalDue
            );
            loan.active = false;

            // Return collateral to user
            IERC20(loan.collateralToken).safeTransfer(
                msg.sender,
                loan.collateralAmount
            );

            emit LoanRepaid(msg.sender, _loanId, totalDue);
            emit CollateralWithdrawn(
                msg.sender,
                _loanId,
                loan.collateralAmount
            );
        } else {
            // Partial repayment
            IERC20(loan.borrowToken).safeTransferFrom(
                msg.sender,
                address(this),
                _amount
            );

            // Reduce principal amount - simple interest model
            // The exact implementation depends on how you want to handle partial repayments
            if (_amount <= loan.borrowAmount) {
                loan.borrowAmount -= _amount;
            } else {
                // Some payment goes toward interest, rest reduces principal
                loan.borrowAmount -= (_amount - (totalDue - loan.borrowAmount));
            }

            emit LoanRepaid(msg.sender, _loanId, _amount);
        }
    }

    function fundPool(address token, uint256 amount) external onlyOwner {
        IERC20(token).safeTransferFrom(msg.sender, address(this), amount);
    }

    function liquidate(address user, uint256 loanId) external nonReentrant {
        Loan storage loan = userLoans[user][loanId];
        require(loan.active, "Loan is not active");

        uint256 threshold = liquidationThresholds[loan.collateralToken];
        require(threshold > 0, "Liquidation threshold not set");

        uint256 totalDue = calculateTotalDue(user, loanId);
        uint256 healthFactor = calculateHealthFactorSimulated(
            loan.collateralAmount,
            totalDue
        );

        require(
            healthFactor < threshold,
            "Health factor above liquidation threshold"
        );

        loan.active = false;

        IERC20(loan.collateralToken).safeTransfer(
            msg.sender,
            loan.collateralAmount
        );

        emit LoanLiquidated(user, loanId, msg.sender);
    }
}

contracts/YieldPool.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "./YieldToken.sol";

/**
 * @title YieldPool
 * @dev manages deposits and yield generation
 */

contract YieldPool is ReentrancyGuard {
    using SafeERC20 for IERC20;
    // State variables that can be configured in initializer
    uint256 private yieldRate;
    uint256 private constant YEAR = 365 days;
    uint256 private minDuration;
    uint256 private maxDuration;

    // State variables
    YieldToken private yieldTokenV2;
    uint256 private totalStakers;
    uint256 private totalValueLocked;
    uint256 private nextPositionId;

    address[] allowedTokens;
    address[] private activeStakers;

    mapping(address => bool) private allowedTokensMap;
    mapping(address => uint256) private activePositionsCount;
    mapping(address => uint256) private stakerIndex;
    mapping(address => mapping(address => uint256)) private userTokenBalance;

    struct Position {
        address positionAddress;
        address token;
        uint256 id;
        uint256 amount;
        uint256 startTime;
        uint256 lockDuration;
        bool withdrawn;
    }

    mapping(address => Position[]) private positions;
    mapping(uint256 => address) private positionOwners;
    address owner = msg.sender;

    modifier onlyOwner() {
        require(msg.sender == owner, "Not the contract owner");
        _;
    }

    // Events
    event Deposited(
        address indexed user,
        address token,
        uint256 amount,
        uint256 duration
    );
    event Withdrawn(
        address indexed user,
        address token,
        uint256 amount,
        uint256 yield
    );
    event TokenAllowedStatusChanged(address token, bool allowed);
    event YieldParametersUpdated(
        uint256 yieldRate,
        uint256 minDuration,
        uint256 maxDuration
    );

    constructor(
        address _yieldTokenAddress,
        uint256 _yieldRate,
        uint256 _minDuration,
        uint256 _maxDuration
    ) {
        // Initialize configurable parameters
        yieldRate = _yieldRate;
        minDuration = _minDuration;
        maxDuration = _maxDuration;

        // Initialize state variables
        yieldTokenV2 = YieldToken(_yieldTokenAddress);
        totalStakers = 0;
        totalValueLocked = 0;
        nextPositionId = 1;

        // Add YieldTokenV2 as first allowed token
        allowedTokens.push(_yieldTokenAddress);
        allowedTokensMap[_yieldTokenAddress] = true;
        emit TokenAllowedStatusChanged(_yieldTokenAddress, true);
    }

    /**
     * @notice updates the yield
     * @param _yieldRate The percentage yield e.g 10%
     * @param _minDuration The minimum duration lock time of tokens deposited.
     * @param _maxDuration The maximum duration lock time of tokens deposited.
     */

    function updateYieldParameters(
        uint256 _yieldRate,
        uint256 _minDuration,
        uint256 _maxDuration
    ) external onlyOwner {
        yieldRate = _yieldRate;
        minDuration = _minDuration;
        maxDuration = _maxDuration;
        emit YieldParametersUpdated(_yieldRate, _minDuration, _maxDuration);
    }

    /**
     * @return yieldTokenV2 the yield token users are going to earn.
     */

    function getYieldToken() external view returns (YieldToken) {
        return yieldTokenV2;
    }

    /**
     * @return yieldRate the percentage yield users are going to earn.
     */

    function getYieldRate() public view returns (uint256) {
        return yieldRate;
    }

    /**
     * @return minDuration the min duration users can stake
     */

    function getMinStakeDuration() public view returns (uint256) {
        return minDuration;
    }

    /**
     * @return getMaxStakeDuration the max duration users can stake
     */

    function getMaxStakeDuration() public view returns (uint256) {
        return maxDuration;
    }

    function getUserTokenBalances()
        public
        view
        returns (address[] memory tokens, uint256[] memory balances)
    {
        uint256 count = 0;

        // count the number of tokens the user has a balance of
        for (uint256 i = 0; i < allowedTokens.length; i++) {
            address token = allowedTokens[i];
            if (userTokenBalance[msg.sender][token] > 0) {
                count++;
            }
        }

        // arrays with the correct size
        tokens = new address[](count);
        balances = new uint256[](count);
        uint256 index = 0;

        // Populate  arrays with the tokens and balances
        for (uint256 i = 0; i < allowedTokens.length; i++) {
            address token = allowedTokens[i];
            if (userTokenBalance[msg.sender][token] > 0) {
                tokens[index] = token;
                balances[index] = userTokenBalance[msg.sender][token];
                index++;
            }
        }

        return (tokens, balances);
    }

    /**
     * @notice updates the yield
     * @param _token address of token to be checked if isAllowed
     * @return boolean if token is allowed (true) or not allowed (false)
     */

    function isTokenAllowed(address _token) public view returns (bool) {
        return allowedTokensMap[_token];
    }

    /**
     * @notice updates the yield
     * @param _newToken  the address of the token being modified.
     * @param _allowed The status of the token being modified true / false
     */
    function modifyAllowedTokens(
        address _newToken,
        bool _allowed
    ) public onlyOwner {
        allowedTokensMap[_newToken] = _allowed;
        emit TokenAllowedStatusChanged(_newToken, _allowed);
    }

    function removeAllowedToken(address _tokenAddress) public onlyOwner {
        uint256 indexToRemove;
        bool found = false;
        for (uint256 index = 0; index < allowedTokens.length; index++) {
            if (allowedTokens[index] == _tokenAddress) {
                indexToRemove = index;
                found = true;
                break;
            }
        }
        require(found, "Token not found in allowed tokens");

        // Check if there are any active positions using this token
        Position[] memory allPositions = getActivePositions();
        for (uint256 i = 0; i < allPositions.length; i++) {
            require(
                allPositions[i].token != _tokenAddress,
                "Token has an active position. Cannot be removed now"
            );
        }

        // Remove the token from the array by swapping it with the last element and popping
        uint256 lastIndex = allowedTokens.length - 1;
        if (indexToRemove != lastIndex) {
            allowedTokens[indexToRemove] = allowedTokens[lastIndex];
        }
        allowedTokens.pop();
        allowedTokensMap[_tokenAddress] = false;

        emit TokenAllowedStatusChanged(_tokenAddress, false);
    }

    /**
     * @notice updates the yield
     * @param _tokenAddress The token address to be added to allowed tokens list
     */

    function addAllowedTokens(address _tokenAddress) public onlyOwner {
        require(_tokenAddress != address(0), "Invalid token address");
        require(
            !isTokenAllowed(_tokenAddress),
            "Token already added and allowed"
        );

        allowedTokens.push(_tokenAddress);
        allowedTokensMap[_tokenAddress] = true;
        emit TokenAllowedStatusChanged(_tokenAddress, true);
    }

    /**
     * @notice get all allowed tokens in  a list
     * @return allowedTokens The token addresses in the allowedTokens list.
     */
    function getAllowedTokens() public view returns (address[] memory) {
        uint256 count = 0;

        // First count how many tokens are allowed
        for (uint256 i = 0; i < allowedTokens.length; i++) {
            if (allowedTokensMap[allowedTokens[i]]) {
                count++;
            }
        }

        // Create array with exact size needed
        address[] memory activeTokens = new address[](count);
        uint256 index = 0;

        // Fill array with allowed tokens
        for (uint256 i = 0; i < allowedTokens.length; i++) {
            if (allowedTokensMap[allowedTokens[i]]) {
                activeTokens[index] = allowedTokens[i];
                index++;
            }
        }

        return activeTokens;
    }

    /**
     * @notice get user position in an active yield pool
     * @param positionId uint256 id of a user position in a yieldPool stakes
     */
    function getPosition(
        uint256 positionId
    ) external view returns (Position memory) {
        address positionOwner = positionOwners[positionId];
        require(positionOwner != address(0), "Position does not exist");

        Position[] memory userPositions = positions[positionOwner];
        for (uint i = 0; i < userPositions.length; i++) {
            if (userPositions[i].id == positionId) {
                return userPositions[i];
            }
        }
        revert("Position not found");
    }

    function getTotalStakers() external view returns (uint256) {
        return totalStakers;
    }

    function getTotalValueLocked() external view returns (uint256) {
        return totalValueLocked;
    }

    /**
     * @notice gets all active positions participating in a yield pool
     * @return activePositions  a list of all active positions.
     */

    function getActivePositions()
        public
        view
        returns (Position[] memory activePositions)
    {
        uint256 totalPositions = 0;
        // count active non-withdrawn positions
        for (uint i = 0; i < activeStakers.length; i++) {
            Position[] memory userPositions = positions[activeStakers[i]];
            for (uint j = 0; j < userPositions.length; j++) {
                if (!userPositions[j].withdrawn) {
                    totalPositions++;
                }
            }
        }

        activePositions = new Position[](totalPositions);
        uint256 currentIndex = 0;

        //  collect all active positions
        for (uint i = 0; i < activeStakers.length; i++) {
            Position[] memory userPositions = positions[activeStakers[i]];
            for (uint j = 0; j < userPositions.length; j++) {
                if (!userPositions[j].withdrawn) {
                    activePositions[currentIndex] = userPositions[j];
                    currentIndex++;
                }
            }
        }

        return activePositions;
    }

    /**
     * @notice Deposits tokens into the yield pool.
     * @param _token The token to deposit
     * @param _amount The amount of tokens to deposit.
     * @param duration The lock duration for the deposit.
     */
    function deposit(
        address _token,
        uint256 _amount,
        uint256 duration
    ) external nonReentrant {
        require(_amount > 0, "Amount must be greater than 0");
        require(
            duration >= minDuration && duration <= maxDuration,
            "Invalid duration"
        );

        require(
            isTokenAllowed(_token),
            "We do not support the tokens you're staking"
        );

        // Save initial balance to verify transfer amount
        uint256 initialBalance = IERC20(_token).balanceOf(address(this));

        // Transfer tokens from user to this contract
        IERC20(_token).safeTransferFrom(msg.sender, address(this), _amount);

        // Verify correct amount was transferred
        uint256 newBalance = IERC20(_token).balanceOf(address(this));
        uint256 actualAmount = newBalance - initialBalance;
        require(actualAmount > 0, "No tokens were transferred");

        // Update state after successful transfer
        totalValueLocked += actualAmount;
        userTokenBalance[msg.sender][_token] += actualAmount;

        if (positions[msg.sender].length == 0) {
            stakerIndex[msg.sender] = activeStakers.length;
            activeStakers.push(msg.sender);
            totalStakers++;
        }

        // Create and add new position
        uint256 positionId = nextPositionId++;
        Position memory newPosition = Position({
            positionAddress: msg.sender,
            token: _token,
            id: positionId,
            amount: actualAmount,
            startTime: block.timestamp,
            lockDuration: duration,
            withdrawn: false
        });

        positions[msg.sender].push(newPosition);
        positionOwners[positionId] = msg.sender;
        activePositionsCount[msg.sender]++;

        emit Deposited(msg.sender, _token, actualAmount, duration);
    }

    /**
     * @notice withdraw tokens from the yield pool.
     * @param positionId uint256 id of an active user having funds in a yieldPool
     */

    function withdraw(uint256 positionId) external nonReentrant {
        // First check if position exists and get owner
        address positionOwner = positionOwners[positionId];
        require(positionOwner != address(0), "Position does not exist");
        require(
            positionOwner == msg.sender,
            "You are not the owner of this position"
        );

        Position[] storage userPositions = positions[msg.sender];
        uint256 positionIndex;
        bool found;

        for (uint i = 0; i < userPositions.length; i++) {
            if (userPositions[i].id == positionId) {
                positionIndex = i;
                found = true;
                break;
            }
        }

        require(found, "Position not found");
        Position storage position = userPositions[positionIndex];
        require(!position.withdrawn, "Already withdrawn");
        require(
            block.timestamp >= position.startTime + position.lockDuration,
            "Still locked"
        );

        address token = position.token;
        uint256 yieldAmount = calculateExpectedYield(
            position.amount,
            position.lockDuration
        );

        // Check if enough tokens are available in the contract
        uint256 tokenBalance = IERC20(token).balanceOf(address(this));

        require(
            tokenBalance >= position.amount + yieldAmount,
            "Insufficient token balance in pool"
        );

        // Update state before external calls
        totalValueLocked -= position.amount;
        userTokenBalance[msg.sender][token] -= position.amount;
        position.withdrawn = true;

        // Transfer yield
        IERC20(token).safeTransfer(msg.sender, position.amount);
        // Transfer tokens last
        IERC20(token).safeTransfer(msg.sender, yieldAmount);

        // Clean up position data
        activePositionsCount[msg.sender]--;

        // Only remove from active stakers if no active positions left
        if (activePositionsCount[msg.sender] == 0) {
            uint256 lastIndex = activeStakers.length - 1;
            address lastStaker = activeStakers[lastIndex];

            if (msg.sender != lastStaker) {
                uint256 currentStakerIndex = stakerIndex[msg.sender];
                activeStakers[currentStakerIndex] = lastStaker;
                stakerIndex[lastStaker] = currentStakerIndex;
            }

            activeStakers.pop();
            totalStakers--;
        }

        // Move this to the end of the function, after transfers
        delete positionOwners[positionId];

        emit Withdrawn(msg.sender, token, position.amount, yieldAmount);
    }

    /**
     * @notice Unstake tokens from the yield pool with penalty if before lock duration.
     * @param positionId uint256 id of an active user having funds in a yieldPool
     */
    function unstake(uint256 positionId) external nonReentrant {
        require(positionOwners[positionId] == msg.sender, "Not position owner");

        Position[] storage userPositions = positions[msg.sender];
        uint256 positionIndex;
        bool found;

        for (uint i = 0; i < userPositions.length; i++) {
            if (userPositions[i].id == positionId) {
                positionIndex = i;
                found = true;
                break;
            }
        }

        require(found, "Position not found");
        Position storage position = userPositions[positionIndex];
        require(!position.withdrawn, "Already withdrawn");

        address token = position.token;
        uint256 amountToReturn = position.amount;

        // 10% penalty if unstaking before lock duration
        if (block.timestamp < position.startTime + position.lockDuration) {
            uint256 penalty = position.amount / 10; // 10% penalty
            amountToReturn = position.amount - penalty;
        }

        // Check if enough tokens in the pool
        uint256 tokenBalance = IERC20(token).balanceOf(address(this));
        require(
            tokenBalance >= amountToReturn,
            "Insufficient token balance in pool"
        );

        // Update state before transfer
        totalValueLocked -= position.amount;
        userTokenBalance[msg.sender][token] -= position.amount;

        // Mark position as withdrawn
        position.withdrawn = true;

        // Clean up position data
        delete positionOwners[positionId];
        activePositionsCount[msg.sender]--;

        // Remove from active stakers if no positions left
        if (activePositionsCount[msg.sender] == 0) {
            uint256 lastIndex = activeStakers.length - 1;
            address lastStaker = activeStakers[lastIndex];

            if (msg.sender != lastStaker) {
                uint256 currentStakerIndex = stakerIndex[msg.sender];
                activeStakers[currentStakerIndex] = lastStaker;
                stakerIndex[lastStaker] = currentStakerIndex;
            }

            activeStakers.pop();
            totalStakers--;
        }

        // Transfer tokens
        IERC20(token).safeTransfer(msg.sender, amountToReturn);

        emit Withdrawn(msg.sender, token, amountToReturn, 0);
    }

    function calculateExpectedYield(
        uint256 _amount,
        uint256 _lockDuration
    ) public view returns (uint256) {
        // unchecked for gas optimization where overflow is impossible
        unchecked {
            return (_amount * _lockDuration * yieldRate) / (YEAR * 100);
        }
    }
}

contracts/YieldToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title YieldToken
 * @dev Represents token.
 */
contract YieldToken is ERC20, Ownable {
    // Track last mint time for each address
    mapping(address => uint256) public lastMintTime;
    mapping(address => bool) public isStudent;
    // Add student status tracking

    mapping(address => bool) public isMinter;
    address[] minters;

    uint256 public constant MINT_COOLDOWN = 1 days;

    /// @custom:oz-upgrades-unsafe-allow constructor
    event MinterSet(address account, bool status);
    // Event for tracking mints
    event TokensMinted(
        address indexed to,
        uint256 amount,
        address indexed minter
    );

    // Custom error for unauthorized minting
    error UnauthorizedMinter(address caller);
    error ZeroAddressMint();


    constructor(
        address initialOwner,
        string memory _name,
        string memory _symbol
    ) ERC20(_name, _symbol) Ownable(initialOwner) {}


    /**
     * @dev Minting specifically for student rewards
     * @param to Address to mint tokens to
     * @param amount Amount of tokens to mint
     */
    function mintForStudent(address to, uint256 amount) external {
        require(isStudent[to], "Address must be a student");
        lastMintTime[to] = block.timestamp;
        _mint(to, amount);
    }

    /**
     * @dev Minting specifically for contract rewards
     * @param to Address to mint tokens to
     * @param amount Amount of tokens to mint
     */
    function InsufficientMint(address to, uint256 amount) external {
        lastMintTime[to] = block.timestamp;
        _mint(to, amount);
    }


    /**
     * @dev Check if address is a  student
     * @param student Address of the student
     */
    function getIsStudent(address student) external view returns (bool) {
        return isStudent[student];
    }
        function setMinter(address account, bool status) external onlyOwner {
        isMinter[account] = status;
        minters.push(account);
        emit MinterSet(account, status);
    }

    function removeMinter(address account) public onlyOwner {
        require(account != address(0), "Invalid address");
        require(isMinter[account], "Address is not a minter");

        // delete instead of setting to false
        delete isMinter[account];

        // Remove from array
        for (uint256 i = 0; i < minters.length; i++) {
            if (minters[i] == account) {
                minters[i] = minters[minters.length - 1];
                minters.pop();
                break;
            }
        }

        emit MinterSet(account, false);
    }

    function getMinters() public view onlyOwner returns (address[] memory) {
        return minters;
    }

    /**
     * @notice Mints new tokens to a specified address
     * @dev Only owner or approved minters can call this function
     * @param to Address to receive the minted tokens
     * @param amount Amount of tokens to mint
     */
    function mint(address to, uint256 amount) public {
        // Check for zero address
        if (to == address(0)) revert ZeroAddressMint();

        // Check if caller is authorized to mint
        if (msg.sender != owner() && !isMinter[msg.sender]) {
            revert UnauthorizedMinter(msg.sender);
        }

        _mint(to, amount);
        emit TokensMinted(to, amount, msg.sender);
    }


function mintToPool(address _yieldPoolAddress) public onlyOwner {
        _mint(_yieldPoolAddress, 10_000_000 * 10 ** 18);
        emit TokensMinted(_yieldPoolAddress, 10_000_000 * 10 ** 18, msg.sender);
    }

    /**
     * @dev Set student status for an address
     * @param student Address of the student
     * @param status True if student, false otherwise
     */
    function setStudentStatus(address student, bool status) external onlyOwner {
        isStudent[student] = status;
    }

    /**
     * @notice only the owner can burn tokens
     * @param from Address of the candidate
     * @param amount Name of the candidate
     */
    function burn(address from, uint256 amount) external onlyOwner {
        _burn(from, amount);
    }
}

